import { Component, OnInit } from '@angular/core';
import { Marca, marcas } from '../../modelo/marca.class';
import { GestionMarcaservice } from './services/gestion-marca.service';

@Component({
  selector: 'app-gestion-marca-main',
  templateUrl: './gestion-marca-main.component.html',
  styleUrls: ['./gestion-marca-main.component.css']
})
export class GestionMarcaMainComponent implements OnInit {

  constructor(
    private gestionmarcaservice:GestionMarcaservice

  ) { 
    this.gestionmarcaservice.listarMarca();
  }

  get listarmarcas(){
    return this.gestionmarcaservice.listar
  }


  nombreMarca:String=""
  nuevaMarca = new Marca(0,this.nombreMarca,[])
  actualizarMarca = new Marca(0,this.nombreMarca,[])

  crearMarca(){
    async (params:any) => {
      this.gestionmarcaservice.agregarmarcaa(this.nuevaMarca);
    } 

    //this.gestionmarcaservice.listarMarca();

  }

  eliminarMarca(id: any){
    this.gestionmarcaservice.eliminarmarcaa(id)
    this.gestionmarcaservice.listarMarca();
  }

  actualizarvariable(marcaactualizado: Marca){
    this.actualizarMarca = marcaactualizado;
    this.gestionmarcaservice.listarMarca();
  }


  actualizarMarcaF(){
    this.gestionmarcaservice.atualizarmarcaa(this.actualizarMarca.codMarca,this.actualizarMarca)
    this.gestionmarcaservice.listarMarca();
    this.gestionmarcaservice.listarMarca();
  }



  ngOnInit(): void {
  }

  eliminacion:number[]=[]

  eliminar(){
    this.gestionmarcaservice.eliminarmarca(this.eliminacion)
    this.eliminacion=[]
  }

  agregar(){
    this.gestionmarcaservice.agregarmarca(this.nombreMarca)
  }

}
<div class="col-s-12">
    <div class="titulo">
        <h1>Producto</h1>
    </div>
</div>
<div class="col-s-12">
    <form  (ngSubmit)="crearMarca()"action="">

        <div class="col-s-6">
            <label for="nombre">Nombre</label>
            <input type="text" 
            id="nombre" 
            placeholder="Nombre" 
            name="nombremarca"
            [(ngModel)]="nuevaMarca.nombreMarca"
            >
        </div>
        <div class="col-s-3 boton">
            <button type="summit">Agregar</button>
        </div>
    </form>

    <form action="">

    <div class="cajat col-s-12">

        <table class="col-s-12">
            <tr>
                <th>Codigo</th>
                <th>Nombre</th>
            </tr>

            <tr *ngFor="let marca of listarmarcas,let i=index">
                <td>{{marca.codMarca}}</td>
                <td>{{marca.nombreMarca}}</td>
                <button (click)="eliminarMarca(marca.codMarca)">Eliminar</button>
                <button (click)="actualizarvariable(marca)">Actualizar</button>
            </tr>
        </table>

    </div>
    </form>
</div>

<h3>Editar</h3>
<form (ngSubmit)="actualizarMarcaF()" action="">
    <table class="col-s-12">

        <tr>
            <td><label for="nombremarca">Nombre</label></td>
            <td><input type="text" id="nombremarca" placeholder="Nombree" name="nombremarcaactualizado"
                     [(ngModel)]="actualizarMarca.nombreMarca"></td>
        </tr>

    </table>

    <div class="col-s-9 boton">
        <button type="summit">Guardar Datos Nuevos</button>
    </div>
</form>
